mapper.select:
/* bidNotice.getBidNoticeMngBidList */ SELECT * , T.BDDPR_BEGIN_DT + ' ~ ' + T.BDDPR_END_DT AS BDDPR_DATE , T.FRST_REGIST_DT + CHAR(10) + '( ' + T.FRST_REGISTER_ID + ' )' AS FRST_REGIST FROM ( SELECT BID_PBLANC_ID -- AS 입찰공고번호 , (SELECT CODE_CHRCTR_REFRNTHREE FROM CO_CMMN_CD D WHERE A.BRAND_GROUP_CODE = D.SUB_CODE AND D.MAIN_CODE = 'BRAND_GROUP_CODE' AND D.CODE_REFRNONE = A.METAL_CODE ) AS METAL_CODE -- AS 메탈코드 , BRAND_CODE -- AS 브랜드_구분 , (SELECT C.ITM_PRDLST_KOREAN FROM IT_ITM_INFO_BAS C WHERE A.METAL_CODE = C.METAL_CODE AND A.ITM_SN = C.ITM_SN ) AS ITM_PRDLST_KOREAN -- AS 상품명 , dbo.fn_getCodeNm('BRAND_GROUP_CODE' , BRAND_GROUP_CODE ) AS BRAND_GROUP_CODE -- AS 브랜드_그룹 , dbo.fn_getCodeNm('DSTRCT_LCLSF_CODE' , DSTRCT_LCLSF_CODE) AS DSTRCT_LCLSF_CODE --AS 권역 , BID_WT -- AS 수량 , '5' AS ITM_QTY -- AS 중량 , CASE WHEN A.BID_STTUS_CODE IN ('12' , '13') THEN dbo.fn_convertDate( BDDPR_BEGIN_DT , 'bo') ELSE SUBSTRING(BDDPR_BEGIN_DT , 0 , 5) + '-' + SUBSTRING(BDDPR_BEGIN_DT , 5 , 2) + '-' + SUBSTRING(BDDPR_BEGIN_DT , 7, 2) END AS BDDPR_BEGIN_DT -- AS 투찰시작일시 , CASE WHEN A.BID_STTUS_CODE IN ('12' , '13' ) THEN dbo.fn_convertDate( BDDPR_END_DT , 'bo') ELSE SUBSTRING(BDDPR_END_DT , 0 , 5) + '-' + SUBSTRING(BDDPR_END_DT , 5 , 2) + '-' + SUBSTRING(BDDPR_END_DT , 7, 2) END AS BDDPR_END_DT -- AS 투찰종료일시 , CASE WHEN DSPY_AT = 'Y' THEN '활성' ELSE '비활성' END AS ACTIVE_AT -- AS 활성여부 , FRST_REGISTER_ID -- AS 최초등록자아이디 , CONVERT(VARCHAR , FRST_REGIST_DT , 120) AS FRST_REGIST_DT -- AS 최초등록날짜 , dbo.fn_getCodeNm('BID_STAT_CODE' , BID_STTUS_CODE) AS BID_STAT_NM -- AS 상태 , BID_STTUS_CODE , ISNULL((SELECT COUNT(BID_ENTRPS_NO) FROM BD_BDDPR_DTL B WHERE B.BID_PBLANC_ID = A.BID_PBLANC_ID AND ISNULL(B.DELETE_AT , 'N') = 'N' AND ISNULL(B.CANCL_AT , 'N') = 'N') ,0) AS BDNG_CMPNY -- AS 투찰기업 , FLOOR(ISNULL((SELECT MIN(BDDPR_PREMIUM_PC) FROM BD_BDDPR_DTL B WHERE A.BID_PBLANC_ID = B.BID_PBLANC_ID AND ISNULL(B.DELETE_AT , 'N') = 'N' AND ISNULL(B.CANCL_AT , 'N') = 'N') , 0)) as LWST_PRPRC -- AS 최저프리미엄 , FLOOR(DELY_CND_01_STDR_PC) AS DELY_CND_01_STDR_PC -- AS 인도조건1 기준가격 , FLOOR(DELY_CND_01_PREMIUM_PC) AS DELY_CND_01_PREMIUM_PC -- AS 인도조건1 프리미엄가격 , FLOOR(DELY_CND_02_STDR_PC) AS DELY_CND_02_STDR_PC -- AS 인도조건2 기준가격 , FLOOR(DELY_CND_02_PREMIUM_PC) AS DELY_CND_02_PREMIUM_PC -- AS 인도조건2 프리미엄가격 , FLOOR(DELY_CND_03_STDR_PC) AS DELY_CND_03_STDR_PC -- AS 인도조건3 기준가격 , FLOOR(DELY_CND_03_PREMIUM_PC) AS DELY_CND_03_PREMIUM_PC -- AS 인도조건3 프리미엄가격 , CONVERT(DATE, CONVERT(VARCHAR(8), DELY_BEGIN_DE)) AS DELY_BEGIN_DE -- AS 인도시작일자 , CONVERT(DATE, CONVERT(VARCHAR(8), DELY_END_DE)) AS DELY_END_DE -- AS 인도종료일자 , CONVERT(DATE, CONVERT(VARCHAR(8), PC_APPN_BEGIN_DE)) AS PC_APPN_BEGIN_DE -- AS 가격지정시작일자 , CONVERT(DATE, CONVERT(VARCHAR(10), PC_APPN_END_DE)) AS PC_APPN_END_DE -- AS 가격지정종료일자 , dbo.fn_getCodeNm('PRICE_SPMTC_CODE' , PC_APPN_MTH_CODE ) AS PC_APPN_MTH_CODE -- AS 가격지정방법코드 , SETLE_CRNCY_CODE -- AS 결제통화코드 , dbo.fn_getCodeNm('PYMNT_MT_CODE' , SETLE_MTH_CODE ) AS SETLE_MTH_CODE -- AS 결제방법코드 , dbo.fn_getCodeNm('PYMNT_PR_CODE' , SETLE_PD_CODE ) AS SETLE_PD_CODE -- AS 결제기간코드 , ETC_CN -- AS 기타내용 , ISNULL(dbo.fn_convertDate(BDDPR_CANCL_LMTT_DE , 'bo'),'') AS BDDPR_CANCL_LMTT_DE -- AS 투찰취소제한일자 , ISNULL(dbo.fn_convertDate(BDDPR_BEGIN_DT , 'bo'),'') AS BDDPR_BEGIN_DT_INFO , ISNULL(dbo.fn_convertDate(BDDPR_END_DT , 'bo'), '') AS BDDPR_END_DT_INFO , ISNULL(FAIL_BID_RESN , '') AS FAIL_BID_RESN -- 유찰사유 , ISNULL(dbo.fn_convertDate(FAIL_BID_DT , 'bo'), '') AS FAIL_BID_DT -- 유찰일시 , ISNULL(dbo.fn_convertDate(PBLANC_CANCL_DT , 'bo') , '') AS PBLANC_CANCL_DT -- 취소일시 , ISNULL(FLOOR(PERM_WT_RATE) , 0) AS PERM_WT_RATE_P -- AS 허용중량용차 FROM BD_BID_BAS A WHERE 1=1 AND ISNULL(DELETE_AT , 'N') = 'N'
AND CONVERT(DATETIME , dbo.fn_convertDate(BDDPR_END_DT , 'bo')) >= CONVERT( DATE , #{startDate})
AND CONVERT(DATETIME , dbo.fn_convertDate(BDDPR_END_DT , 'bo')) <= DATEADD( DAY , 1 , CONVERT(DATE , #{endDate}))
AND BID_STTUS_CODE = #{bidSttusCode}
AND BID_PBLANC_ID like '%' + #{bidPblancId} + '%'
) T ORDER BY T.FRST_REGIST_DT DESC

/* bidNotice.getBidNoticeMngBidInfo */ SELECT * , T.BDDPR_BEGIN_DT + ' ~ ' + T.BDDPR_END_DT AS BDDPR_DATE , T.FRST_REGIST_DT + CHAR(10) + '( ' + T.FRST_REGISTER_ID + ' )' AS FRST_REGIST FROM ( SELECT BID_PBLANC_ID -- AS 입찰공고번호 , (SELECT CODE_CHRCTR_REFRNTHREE FROM CO_CMMN_CD D WHERE A.BRAND_GROUP_CODE = D.SUB_CODE AND D.MAIN_CODE = 'BRAND_GROUP_CODE' AND D.CODE_REFRNONE = A.METAL_CODE ) AS METAL_CODE -- AS 메탈코드 , BRAND_CODE -- AS 브랜드_구분 , (SELECT C.ITM_PRDLST_KOREAN FROM IT_ITM_INFO_BAS C WHERE A.METAL_CODE = C.METAL_CODE AND A.ITM_SN = C.ITM_SN ) AS ITM_PRDLST_KOREAN -- AS 상품명 , dbo.fn_getCodeNm('BRAND_GROUP_CODE' , BRAND_GROUP_CODE ) AS BRAND_GROUP_CODE -- AS 브랜드_그룹 , dbo.fn_getCodeNm('DSTRCT_LCLSF_CODE' , DSTRCT_LCLSF_CODE) AS DSTRCT_LCLSF_CODE --AS 권역 , BID_WT -- AS 수량 , '5' AS ITM_QTY -- AS 중량 , CASE WHEN A.BID_STTUS_CODE IN ('12' , '13') THEN dbo.fn_convertDate( BDDPR_BEGIN_DT , 'bo') ELSE SUBSTRING(BDDPR_BEGIN_DT , 0 , 5) + '-' + SUBSTRING(BDDPR_BEGIN_DT , 5 , 2) + '-' + SUBSTRING(BDDPR_BEGIN_DT , 7, 2) END AS BDDPR_BEGIN_DT -- AS 투찰시작일시 , CASE WHEN A.BID_STTUS_CODE IN ('12' , '13' ) THEN dbo.fn_convertDate( BDDPR_END_DT , 'bo') ELSE SUBSTRING(BDDPR_END_DT , 0 , 5) + '-' + SUBSTRING(BDDPR_END_DT , 5 , 2) + '-' + SUBSTRING(BDDPR_END_DT , 7, 2) END AS BDDPR_END_DT -- AS 투찰종료일시 , CASE WHEN DSPY_AT = 'Y' THEN '활성' ELSE '비활성' END AS ACTIVE_AT -- AS 활성여부 , FRST_REGISTER_ID -- AS 최초등록자아이디 , CONVERT(VARCHAR , FRST_REGIST_DT , 120) AS FRST_REGIST_DT -- AS 최초등록날짜 , dbo.fn_getCodeNm('BID_STAT_CODE' , BID_STTUS_CODE) AS BID_STAT_NM -- AS 상태 , BID_STTUS_CODE , ISNULL((SELECT COUNT(BID_ENTRPS_NO) FROM BD_BDDPR_DTL B WHERE B.BID_PBLANC_ID = A.BID_PBLANC_ID AND ISNULL(B.DELETE_AT , 'N') = 'N' AND ISNULL(B.CANCL_AT , 'N') = 'N') ,0) AS BDNG_CMPNY -- AS 투찰기업 , FLOOR(ISNULL((SELECT MIN(BDDPR_PREMIUM_PC) FROM BD_BDDPR_DTL B WHERE A.BID_PBLANC_ID = B.BID_PBLANC_ID AND ISNULL(B.DELETE_AT , 'N') = 'N' AND ISNULL(B.CANCL_AT , 'N') = 'N') , 0)) as LWST_PRPRC -- AS 최저프리미엄 , FLOOR(DELY_CND_01_STDR_PC) AS DELY_CND_01_STDR_PC -- AS 인도조건1 기준가격 , FLOOR(DELY_CND_01_PREMIUM_PC) AS DELY_CND_01_PREMIUM_PC -- AS 인도조건1 프리미엄가격 , FLOOR(DELY_CND_02_STDR_PC) AS DELY_CND_02_STDR_PC -- AS 인도조건2 기준가격 , FLOOR(DELY_CND_02_PREMIUM_PC) AS DELY_CND_02_PREMIUM_PC -- AS 인도조건2 프리미엄가격 , FLOOR(DELY_CND_03_STDR_PC) AS DELY_CND_03_STDR_PC -- AS 인도조건3 기준가격 , FLOOR(DELY_CND_03_PREMIUM_PC) AS DELY_CND_03_PREMIUM_PC -- AS 인도조건3 프리미엄가격 , CONVERT(DATE, CONVERT(VARCHAR(8), DELY_BEGIN_DE)) AS DELY_BEGIN_DE -- AS 인도시작일자 , CONVERT(DATE, CONVERT(VARCHAR(8), DELY_END_DE)) AS DELY_END_DE -- AS 인도종료일자 , CONVERT(DATE, CONVERT(VARCHAR(8), PC_APPN_BEGIN_DE)) AS PC_APPN_BEGIN_DE -- AS 가격지정시작일자 , CONVERT(DATE, CONVERT(VARCHAR(10), PC_APPN_END_DE)) AS PC_APPN_END_DE -- AS 가격지정종료일자 , dbo.fn_getCodeNm('PRICE_SPMTC_CODE' , PC_APPN_MTH_CODE ) AS PC_APPN_MTH_CODE -- AS 가격지정방법코드 , SETLE_CRNCY_CODE -- AS 결제통화코드 , dbo.fn_getCodeNm('PYMNT_MT_CODE' , SETLE_MTH_CODE ) AS SETLE_MTH_CODE -- AS 결제방법코드 , dbo.fn_getCodeNm('PYMNT_PR_CODE' , SETLE_PD_CODE ) AS SETLE_PD_CODE -- AS 결제기간코드 , ETC_CN -- AS 기타내용 , ISNULL(dbo.fn_convertDate(BDDPR_CANCL_LMTT_DE , 'bo'),'') AS BDDPR_CANCL_LMTT_DE -- AS 투찰취소제한일자 , ISNULL(dbo.fn_convertDate(BDDPR_BEGIN_DT , 'bo'),'') AS BDDPR_BEGIN_DT_INFO , ISNULL(dbo.fn_convertDate(BDDPR_END_DT , 'bo'), '') AS BDDPR_END_DT_INFO , ISNULL(FAIL_BID_RESN , '') AS FAIL_BID_RESN -- 유찰사유 , ISNULL(dbo.fn_convertDate(FAIL_BID_DT , 'bo'), '') AS FAIL_BID_DT -- 유찰일시 , ISNULL(dbo.fn_convertDate(PBLANC_CANCL_DT , 'bo') , '') AS PBLANC_CANCL_DT -- 취소일시 , ISNULL(FLOOR(PERM_WT_RATE) , 0) AS PERM_WT_RATE_P -- AS 허용중량용차 FROM BD_BID_BAS A WHERE 1=1 AND ISNULL(DELETE_AT , 'N') = 'N'
AND CONVERT(DATETIME , dbo.fn_convertDate(BDDPR_END_DT , 'bo')) >= CONVERT( DATE , #{startDate})
AND CONVERT(DATETIME , dbo.fn_convertDate(BDDPR_END_DT , 'bo')) <= DATEADD( DAY , 1 , CONVERT(DATE , #{endDate}))
AND BID_STTUS_CODE = #{bidSttusCode}
AND BID_PBLANC_ID like '%' + #{bidPblancId} + '%'
) T ORDER BY T.FRST_REGIST_DT DESC

/* bidNotice.getBidNoticeMngBidUpdtList */ SELECT BID_UPDT_CN -- AS 입찰수정내용 , BID_UPDT_RESN -- AS 입찰수정사유 , CONVERT(VARCHAR , FRST_REGIST_DT , 120) AS FRST_REGIST_DT -- AS 최초등록일시 FROM BD_BID_UPDT_HST WHERE 1=1
AND BID_PBLANC_ID = #{bidPblancId}
ORDER BY BID_UPDT_HST_SN

/* bidNotice.getBidNoticeMngBidBddprDtlList */ SELECT T.* , CASE WHEN T.BID_STTUS_CODE IN ('30','31') AND ISNULL(T.SCSBID_AT ,'N') = 'Y' THEN '낙찰' WHEN T.BID_STTUS_CODE IN ('30','31') AND ISNULL(T.SCSBID_AT ,'N') = 'N' THEN '패찰' ELSE dbo.fn_getCodeNm('BID_STAT_CODE' , T.BID_STTUS_CODE) END AS BID_STAT_NM FROM ( SELECT (SELECT ENTRPS_NM FROM BD_ENTRPS_INFO_BAS B WHERE B.BID_ENTRPS_NO = A.BID_ENTRPS_NO) AS ENTRPS_NM , CONVERT(VARCHAR , A.FRST_REGIST_DT , 120) AS FRST_REGIST_DT , FLOOR(BDDPR_PREMIUM_PC) AS BDDPR_PREMIUM_PC , (SELECT BID_STTUS_CODE FROM BD_BID_BAS WHERE BID_PBLANC_ID = #{bidPblancId}) AS BID_STTUS_CODE , ROW_NUMBER() OVER (ORDER BY A.BDDPR_PREMIUM_PC , A.FRST_REGIST_DT) ROWNUM , DELY_CND_CODE , dbo.fn_getCodeNm('DLVRY_CN_CODE',DELY_CND_CODE) AS DELY_CND_CODE_NM , SCSBID_AT FROM BD_BDDPR_DTL A WHERE 1=1 AND ISNULL(DELETE_AT , 'N') = 'N' AND ISNULL(CANCL_AT , 'N') = 'N'
AND BID_PBLANC_ID = #{bidPblancId}
)T ORDER BY T.ROWNUM

mapper.update:
/* bidNotice.bidBidNoticeCanlcel */
UPDATE BD_BID_BAS
   SET BID_STTUS_CODE = #{bidSttusCode}
     , LAST_CHANGE_DT = SYSDATETIME()

     , FAIL_BID_RESN = #{failBidResn}
     , FAIL_BID_DT = FORMAT(cast(getdate() as datetime), 'yyyyMMddHHmmss')


     , PBLANC_CANCL_DT = FORMAT(cast(getdate() as datetime), 'yyyyMMddHHmmss')

 WHERE BID_PBLANC_ID = #{bidPblancId}

mapper.select:
/* bidNotice.getBidBddprDtlCnt */
SELECT COUNT(*) AS CNT
  FROM BD_BDDPR_DTL
 WHERE BID_PBLANC_ID = #{bidPblancId}
   AND ISNULL(DELETE_AT , 'N') = 'N'

mapper.update:
/* bidNotice.updateDelBidNotice */
UPDATE BD_BID_BAS
   SET DELETE_AT = 'Y'
     , DELETE_DT = SYSDATETIME()
     , LAST_CHANGE_DT = SYSDATETIME()
 WHERE BID_PBLANC_ID = #{bidPblancId}
